actionConfiguration.body:
SELECT *
FROM public.monsters
-- check for randomness control
{{(function() {
	var ret=" ";
	if (appsmith.store.randomness_control_current.length == 1) {
				switch(appsmith.store.randomness_control_current[0]) {
						case 'only_without_entered_ddb_desc':
							ret = "WHERE ddb_description is NULL and has_description_on_ddb is NULL";
							break;
						case 'only_without_ddb_desc':
							ret = "WHERE ddb_description is NULL and has_description_on_ddb is false";
							break;
						case 'only_with_ddb_desc':
							ret = "WHERE ddb_description <> ''";
							break;
						default:
							ret=" ";
				}
			}
	return ret;
	})()
}}
ORDER BY random()
LIMIT 1;

