actionConfiguration.body:
() => {
		if (rand_control_switch_group.selectedValues.length >= 1) {
			if (appsmith.store.randomness_control_current[0] != ""){
				var unique_select = rand_control_switch_group.selectedValues.filter(function(val) {
					return appsmith.store.randomness_control_current.indexOf(val) == -1;
				});
				if (unique_select.length == 1) {
					storeValue("randomness_control_current", unique_select);
					resetWidget("rand_control_switch_group", true);
				} else {
					storeValue("randomness_control_current", appsmith.store.randomness_control_default);
					resetWidget("rand_control_switch_group", true);
				}
			} else {
				storeValue("randomness_control_current", rand_control_switch_group.selectedValues);
				resetWidget("rand_control_switch_group", true);
			}
		} else {
			storeValue("randomness_control_current", appsmith.store.randomness_control_default);
			resetWidget("rand_control_switch_group", true);
		}
	}

